interface IData {
  [props: string]: any;
}

interface ILogs {
  type: string,
  subType: string;
  msg: IData;
  time: number;
 }

declare module "@xmly/binary-report" {
    /**
     * @param url {string} 上报地址
     * @param globalData {object} 环境数据
     * @param logs {array} 上报数据数组
     * @param useBeacon {boolean} 是否用beacon上报
     */
    const binaryReport: <T>(url: string, globalData: IData, logs: ILogs[], useBeacon?: T) => T extends Boolean ? null : XMLHttpRequest ;
    export default binaryReport;

    export const getSendData: (globalData: IData, logs: ILogs[]) => ArrayBuffer;
}
