/**
 * @description: 初始化参数
 * @param {boolean} autoExpo 是否开启曝光埋点
 * @return {boolean} autoPageTracker 是否开启页面周期埋点
 */
export interface Options {
    autoTrack?: Function;
    autoExpo?: boolean;
    autoPageTracker?: boolean;
    isDebugger?: DEBUGGER;
    xmrep?: xmrepOptions;
    router?: routerOptions;
}
export declare type routerOptions = {
    type: 'browser' | 'hash';
};
declare type xmrepOptions = {
    start?: any;
    params?: any;
};
export declare type DEBUGGER = true | false;
/**
 * @description: 埋点注册表
 */
export interface TrackerMap {
    [id: string]: (obj: any, trackParams?: TrackParams) => void;
}
/**
 * @description: 处理后埋点表结构
 */
export interface TrackerEvent {
    id: string;
    label: string;
    pathname?: string;
    cb: (obj: any, trackParams?: TrackParams) => void;
}
/**
 * @description: 埋点上报数据
 */
export interface TrackParams {
    evId: string | string[];
    evParams?: object | null;
    evTrigger?: string;
}
export interface RouterObject {
    refererPath?: string;
    currentPath?: string;
    ext: {
        referer?: Location | HashChangeEvent;
        current: Location;
        action: string;
    };
}
export {};
