import report from '../share/report';
import { formatTimeData } from '../share/data';
import { repTypes } from '../config';
/**
 * 分享状态上报
 */
function state(data) {
    record(data, repTypes.SHARE.subTypes.STATE);
}
/**
 * 分享回流上报
 */
function feedback(data) {
    record(data, repTypes.SHARE.subTypes.FEEDBACK);
}
function record(data, subType) {
    report({
        subType: subType,
        type: repTypes.SHARE,
        data: [formatTimeData(data)],
    });
}
export var share = { state: state, feedback: feedback };
