/**  获取时间 */
export function getTimestamp() {
    return +new Date();
}
/**
 * 生成uuid
 */
var dec2hex = "0123456789abcdef";
export function generateUUID() {
    var uuid = "";
    for (var i = 1; i <= 36; i++) {
        if (i === 9 || i === 14 || i === 19 || i === 24) {
            uuid += "-";
        }
        else if (i === 15) {
            uuid += 4;
        }
        else if (i === 20) {
            uuid += dec2hex[(Math.random() * 4) | (0 + 8)];
        }
        else {
            uuid += dec2hex[(Math.random() * 16) | 0];
        }
    }
    return uuid;
}
/**
 * 比较两个版本号，需要两个版本号长度相同，1.0 和 1.1 或者 1.0.1 和 1.1.0
 * @param first
 * @param second
 * @return first > second => 1 first === second => 0 first < second => -1
 */
export var compareVersion = function (first, second) {
    var fp = first.split('.');
    var sp = second.split('.');
    var ffp = fp.shift();
    var fsp = sp.shift();
    if (Number(ffp) > Number(fsp))
        return 1;
    if (Number(ffp) < Number(fsp))
        return -1;
    // all parts equals
    if (fp.length === 0 && sp.length === 0) {
        return 0;
    }
    return compareVersion(fp.join('.'), sp.join('.'));
};
